sysevent_script_action.script:
sendInfo();

function sendInfo() {
    var number = event.parm1;
    gs.log("script action is being triggered.");
    gs.info("this is the number of deleted record: " + number);
    try {
        var r = new sn_ws.RESTMessageV2('x_58872_needit.UpdateInfo', 'POST method');
        r.setStringParameterNoEscape('number', current.number);
        gs.info("The number of instance being updated is" + current.number);
        var response = r.execute();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();
    } catch (ex) {
        var message = ex.message;
    }
}
